public class Kundendatenbank implements DatenbankInterface {

    private HashMap<Integer, String> kunden;

    public Kundendatenbank(HashMap<Integer, String> kunden) {
        this.kunden = kunden;
    }

    public String kundenNameGeben(int kundennummer) {
        return kunden.get(kundennummer);
    }

    public void kundenNameEinfuegen(int kundennummer, String name) {
        kunden.put(kundennummer, name);
    }
}

public interface DatenbankInterface {
    String kundenNameGeben(int kundennummer);
    void kundenNameEinfuegen(int kundennummer, String name);
}

public class DatenbankProxy implements DatenbankInterface{
    private boolean eingeloggt;
    private LinkedList<Integer> mitarbeiter;
    private Kundendatenbank kundendatenbank;

    public DatenbankProxy(LinkedList<Integer> mitarbeiter, Kundendatenbank Kundendatenbank) {
        this.mitarbeiter = mitarbeiter;
        this.kundendatenbank = kundendatenbank;
        eingeloggt = false;
    }

    // TODO: Der Mitarbeiter soll eingeloggt werden, wenn die ID in der Liste einhalten ist.
    // Tipp: mitarbeiter.contains(id)
    public void login(int id) { }

    // TODO: Wenn ein Mitarbeiter eingeloggt ist, soll dieser ausgeloggt werden.
    public void logout() { }

    // TODO
    public boolean istEingeloggt() {
        return false;
    }

    // TODO: Wenn jemand eingeloggt ist, soll die entsprechende Methode in der Kundendatenbank aufgerufen werden.
    public String kundenNameGeben(int kundennummer) {
        return "";
    }

    // TODO: Wenn jemand eingeloggt ist, soll die entsprechende Methode in der Kundendatenbank aufgerufen werden.
    public void kundenNameEinfuegen(int kundennummer, String name) { }
}

public class Applikation {
    public static void main(String[] args) {
        HashMap<Integer, String> kunden = new HashMap<>();
        kunden.put(1, "Helmut");
        kunden.put(2, "Alfons");
        kunden.put(3, "Stephan");
        kunden.put(4, "Bernd");
        kunden.put(5, "Pramod");
        kunden.put(6, "Jan Philip");
        kunden.put(7, "Matthias");
        kunden.put(8, "Evgeny");

        LinkedList<Integer> mitarbeiter = new LinkedList<>();
        mitarbeiter.add(934543895);
        mitarbeiter.add(37984283);

        DatenbankProxy datenbank = new DatenbankProxy(mitarbeiter, new Kundendatenbank(kunden));

        datenbank.kundenNameGeben(1);
        datenbank.login(37984283);
        datenbank.kundenNameGeben(1);
    }
}
